import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class UtilisateurService {

  constructor() { }
  users = [{
    nom :'Bataillion',
    prenom:'Victor',
    statut:'Activé'
  },{
    nom :'DE VAUJUAS',
    prenom:'Yann',
    statut:'Desactivé'
  },{
    nom :'Hautin',
    prenom:'Raphael',
    statut:'Desactivé'
  }];
  activerUser(){
    /*
    -- Java
    for(Utilisateur utilisateur:utilisateurs){
      utilisateur.statut = "Activé";
    }
    */
    for(let user of this.users){
      user.statut = "Activé";
    }
  }
  desactiverUser(){
    for(let user of this.users){
      user.statut = "Desactivé"
    }
  }
}
