import { Component, OnInit } from '@angular/core';
import { UtilisateurService } from '../services/utilisateur.service';

@Component({
  selector: 'app-utilisateur',
  templateUrl: './utilisateur.component.html',
  styleUrls: ['./utilisateur.component.css'],
  providers: [] // Les services
})
export class UtilisateurComponent implements OnInit{
  // Déclaration d'un tableau d'utilisateurs
  // ! : le tableau n'est pas initialisé
  users!:any[]; // any : n'importe quel type de données
  // DI : par constructeur  
  constructor(private utilisateurService:UtilisateurService){
  }
  ngOnInit(): void {
    this.users = this.utilisateurService.users;
  }
  onActiverUser(){
    this.utilisateurService.activerUser();
  }
  onDesactiverUser(){
    this.utilisateurService.desactiverUser();
  }
}
